const HomeScreen = ({ navigation }) => {
  return (
    <View style={styles.container}>
      <Text style={styles.header}>FAVORITE MOVIES</Text>
      <FlatList
        data={moviesData}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <TouchableOpacity
            style={styles.movieItem}
            onPress={() => navigation.navigate("Details", { movie: item })}
          >
            <Image source={{ uri: item.image }} style={styles.movieImage} />
            <Text style={styles.movieTitle}>{item.title}</Text>
          </TouchableOpacity>
        )}
      />
      <TouchableOpacity style={styles.addButton} onPress={() => navigation.navigate("AddMovie")}> 
        <Text style={styles.addButtonText}>+ Add Movie</Text>
      </TouchableOpacity>
    </View>
  );
};

const DetailsScreen = ({ route }) => {
  const { movie } = route.params;
  return (
    <View style={styles.container}>
      <Image source={{ uri: movie.image }} style={styles.detailImage} />
      <Text style={styles.detailTitle}>{movie.title}</Text>
      <Text style={styles.detailDescription}>{movie.description}</Text>
    </View>
  );
};

const AddMovieScreen = ({ navigation }) => {
  const [title, setTitle] = useState("");
  const [image, setImage] = useState("");
  const [description, setDescription] = useState("");

  const addMovie = () => {
    if (title && image && description) {
      moviesData.push({ id: (moviesData.length + 1).toString(), title, image, description });
      navigation.goBack();
    }
  };

  return (
    <View style={styles.container}>
      <TextInput style={styles.input} placeholder="Movie Title" onChangeText={setTitle} />
      <TextInput style={styles.input} placeholder="Image URL" onChangeText={setImage} />
      <TextInput style={styles.input} placeholder="Description" onChangeText={setDescription} />
      <TouchableOpacity style={styles.addButton} onPress={addMovie}>
        <Text style={styles.addButtonText}>Add Movie</Text>
      </TouchableOpacity>
    </View>
  );
};

const Stack = createStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Home" component={HomeScreen} />
        <Stack.Screen name="Details" component={DetailsScreen} />
        <Stack.Screen name="AddMovie" component={AddMovieScreen} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}

